'use strict';

/**
 * Module dependencies.
 */
var express = require('express'),
    expressLoad = require('express-load');

var app = express();

// Express Settings
require('./config/express')(app);

// Bootstrap app
expressLoad('test/routes', {
    extlist: /(.*)\.(js$)/
}).into(app);

var port = 3011;
app.listen(port);
console.log('Test Express app started on port ' + port);

// Expose app
module.exports = app;
